import React from 'react'
import App from './App'
import styled from 'styled-components'

const WeeklyScore = (props) => {
    const [score, setScore] = React.useState(5);
    console.log(score);
    const daily_score = Array.from({length: score}, (v, i) => i);

    return (
        <MyWrap>
            <h1>내 일주일은?</h1>
            <MyWeek>
                <MyDays>
                    <div>월</div> 
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
                <MyDays>
                    <div>화</div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
                <MyDays>
                    <div>수</div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
                <MyDays>
                    <div>목</div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
                <MyDays>
                    <div>금</div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
                <MyDays>
                    <div>토</div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
                <MyDays>
                    <div>일</div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='circle'></div>
                    <div className='triangle'></div>
                </MyDays>
            </MyWeek>
        </MyWrap>
    )

}

const MyWrap = styled.div`
    width: 350px;
    height: 700px;
    text-align: center;
    align-itmes: center;

    margin: 0px auto;

    h1{
        color: darkslateblue;
    }
`
const MyWeek = styled.div`
    width: 300px;
    margin: 0 auto;
`
const MyDays = styled.div`
    font-size: 1.5em;

    display: flex;
    flex-direction: row;

    padding: 15px;
    .circle{
        width : 30px;
        height : 30px;
        border-radius: 50%;
        background-color: orange;

        float: left;
        margin: 0px 5px;
    }
    .triangle {
        width: 0px;
        height: 0px;
        border-left: 32px solid red;
        border-top: 16px solid transparent;
        border-bottom: 16px solid transparent;
        float: left;
        margin: 0px 5px;
    }
`

export default WeeklyScore;
