import React from 'react'
import App from './App'
import styled from 'styled-components'

const WeeklyScore = () => {
    return (
        <MyWrap>
            <h1>내 일주일은?</h1>
            <MyDays>
                <div>월</div> 
                <div className='circle'></div>
                <div className='circle'></div>
                <div className='circle'></div>
                <div className='circle'></div>
                <div className='circle'></div>
                <div className='triangle'></div>
            </MyDays>
            <MyDays>
                <div>화</div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div>
            </MyDays>
            <MyDays>
                <div>수</div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div>
            </MyDays>
            <MyDays>
                <div>목</div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div>
            </MyDays>
            <MyDays>
                <div>금</div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div>
            </MyDays>
            <MyDays>
                <div>토</div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div>
            </MyDays>
            <MyDays>
                <div>일</div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div><div className='circle'></div>
            </MyDays>

        </MyWrap>
    )

}

const MyWrap = styled.div`
    width: 400px;
    height: 700px;
    text-align: center;
    margin: 0px auto;

    h1{
        color: darkslateblue;
    }
`
const MyDays = styled.div`
    font-size: 1.5em;

    display: flex;
    flex-direction: row;

    padding: 15px;
    .circle{
        width : 30px;
        height : 30px;
        border-radius: 50%;
        background-color: orange;

        float: left;
        margin: 0px 10px;
    }
    .triangle {
        width: 0px;
        height: 0px;
        border-bottom: calc( 20px * 1.732 ) solid red;
        border-left: 20px solid transparent;
        border-right: 20px solid transparent;
        float: left;
        margin: 0px 10px;
    }
`

export default WeeklyScore;
