import React from 'react'
import App from './App'
import styled from 'styled-components'
import { Link } from 'react-router-dom';

const WeeklyScore = (props) => {
    const [score, setScore] = React.useState(5);  //초기화 score 값 5 // useState : 함수형 컴포넌트 
    //console.log(score);

    // const randomScore = () => {
    //     setScore(Math.floor(Math.random() * 5) + 1);
    // }
    // console.log(randomScore);

    const daily_score = Array.from({length: 5}, (v, i) => i);

    return (
        <>
        <MyWrap>
            <h1>내 일주일은?</h1>
            <MyWeek>
                <MyDays>
                    <div>월</div> 
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/월"><div className='triangle'></div></Link>
                </MyDays>
                <MyDays>
                    <div>화</div>
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/화"><div className='triangle'></div></Link>
                </MyDays>
                <MyDays>
                    <div>수</div>
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/수"><div className='triangle'></div></Link>                
                </MyDays>
                <MyDays>
                    <div>목</div>
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/목"><div className='triangle'></div></Link>
                </MyDays>
                <MyDays>
                    <div>금</div>
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/금"><div className='triangle'></div></Link>
                </MyDays>
                <MyDays>
                    <div>토</div>
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/토"><div className='triangle'></div></Link>
                </MyDays>
                <MyDays>
                    <div>일</div>
                    {daily_score.map((n, i) => {
                        return (
                            <div key={i} className='circle'></div>
                        )
                    })}
                    <Link to="/daily/일"><div className='triangle'></div></Link>
                </MyDays>
            </MyWeek>
        </MyWrap>
        </>
    )

}

const MyWrap = styled.div`
    width: 350px;
    height: 700px;
    text-align: center;
    align-itmes: center;

    margin: 0px auto;

    h1{
        color: darkslateblue;
    }
`
const MyWeek = styled.div`
    width: 300px;
    margin: 0 auto;
`
const MyDays = styled.div`
    font-size: 1.5em;

    display: flex;
    flex-direction: row;

    padding: 15px;
    .circle{
        width : 30px;
        height : 30px;
        border-radius: 50%;
        background-color: black;

        float: left;
        margin: 0px 5px;
    }
    .triangle {
        width: 0px;
        height: 0px;
        border-left: 32px solid red;
        border-top: 16px solid transparent;
        border-bottom: 16px solid transparent;
        float: left;
        margin: 0px 5px;
    }
`

export default WeeklyScore;
